@import url('https://fonts.googleapis.com/css2?family=Roboto&display=swap');

$header-bg-color: #123321;
$footer-bg-color: #123321;
$container-width: 960px;
$character-card-bg-color: rgb(138, 248, 165);
$paladin:#f48cba;
body {
  font-family: 'Roboto', sans-serif;
  padding: 0px;
  margin: 0px;
  box-sizing: border-box;
  // background-color: rgb(233, 255, 228);
}

header {
  background-color: $header-bg-color;
  color: #fff;
}

section {
  margin-top: 20px;
}

footer {
  background-color: $footer-bg-color;
  margin-top: 10px;
  color: #fff;
  min-height: 24vh;

  .container {
    display: flex;
    flex-wrap: wrap;
    justify-content: space-evenly;
  }
}

.container  {
  max-width: 960px;
  margin: 0 auto;
  display: flex;
}

.header {
  align-items: center;
  display: flex;
  justify-content: space-between;
  padding: 10px 40px;
  width: 100%;


  &__logo {
    width: 8rem;
  }
}

.main-content {
  width: 66%;
  display: flex;
  justify-content: space-evenly;
  flex-wrap: wrap;

  .welcome-frame {
    background: url("/img/welcome.jpg") no-repeat;
    background-color: red;
    border-radius: 6px;
    margin-right: 10px;
    width: 100%;

    h2 {
      padding: 20px;
      color: rgb(253, 255, 152);
      display: inline-block;
      background-color: rgba($color: #042211, $alpha:.8);
    }
  }

  .character-card {
    display: flex;
    flex-direction: column;
    max-width: 11rem;
    max-height: 360px;
    background-color: $character-card-bg-color;
    padding: 8px;
    margin-top: 10px;
    box-shadow: 3px 3px 3px #123321;
    border-radius: 8px;
    
    &__name {
      align-self: center;
    }

    img {
      max-width: 200px;
      max-height: 200px;
      border-radius: 6px;
      background-color: red;
    }

    &:hover {
      background-color:  rgb(121, 216, 145);
      // cursor: pointer;
      transition: .2s background-color ease-in;
    }

    span {
      margin: 4px;
      border-bottom: 1px solid black;
    }
  }
}

.sidebar {
  width: 33%;
  border-radius: 6px;
  box-shadow: 0px 5px 10px #123321;
  padding: 10px;

  .parameters {
    display: flex;
    flex-direction: column;
    
    label {
      margin: 10px 0px;
      position: relative;
      display: flex;
      justify-content: space-between;
      font-size: 1.2rem;
      margin-top: 20px;
      border-bottom: 1px solid rgb(0,0,0, 0.3);

      &:last-child {
      border-bottom: none;
      display: block;
      text-align: center;
      }

      .validate-alert {
        position: absolute;
        display: none;
        font-size: 0.8rem;
        top: -20px;
        right: 0px;
        color: red;
        transition: .3s display cubic-bezier(0.075, 0.82, 0.165, 1);
      }


      input {
        min-height: 24px;
        line-height: 24px;
        font-size: 1.1rem;
        border-radius: 6px;
        padding: 6px;
        border: none;

        &[type="text"] {
          max-width: 190px;

        }
      }

      select {
        font-size: 1.2rem;
        padding: 6px;
        border-radius: 6px;
        border:none;
        cursor: pointer;
      }

      .age {
        width: 20%;
      }
    }

    &__gender-wrapper {
      display: flex;
      align-items: center;
    }

    &__button {
      font-size: 1.3rem;
      border: 1px solid #123321;
      border-radius: 6px;
      padding: 6px;
      
      
      &:hover {
        cursor: pointer;
        background-color: #123321;
        color: #fff;
        transition: .2s background-color ease-in;
        transition: .2s color ease-in;

      }
    }
  }
  
  .party-is-ready {
    text-align:  center;
    font-size: 1.2rem;
  }

}

.additional-content {
  font-size: 1.2rem;

  .question {
    display: block;
    padding: 10px;
    width: 100%;
    min-height: 378px;
    margin: 0 auto;
    background-color: #b1f3ff;
    border-radius: 6px;


    &__heading {
      text-align: center;
    }

    &__text {
      display: block;
      margin: 10px auto;
      text-align: center;
    }

    &__answers {
      display: flex;
      align-items: center;
      justify-content: space-evenly;

      .answer-item {
        cursor: pointer;
        border-radius: 6px;
        margin: 10px;
        padding: 10px;
        background-color: #123321;
        color: #fff;
        display: flex;
        flex-direction: column;
        align-items: center;
      }

      img {
        max-width: 160px;
        max-height: 180px;
        border-radius: 6px;
        margin-bottom: 10px;
      }
    }

    .answer-wrap {
      display: flex;
      justify-content: space-between;
      padding: 30px;

      .secret-card {
        display: flex;
        flex-direction: column;
        max-width: 180px;
        padding: 10px;
        background-color: $paladin;
        border: 1px solid $paladin;
        border-radius: 6px;
        box-shadow: 0px 2px 10px #000;
  
        img {
          max-width: 100%;
        }
      }
    }
    
    .reject-wrap {
      display: flex;
      flex-wrap: wrap;
      justify-content: space-evenly;
      padding: 20px;


      img {
        max-width: 380px;
        border-radius: 50%;
      }
    }

    .process-wrap {
      display: block;
      max-width: 50%;
      margin: auto;
      border: 1px solid #fff;
      border-radius: 6px;
      margin-top: 100px;
      text-align: center;
      min-height: 142px ;
      

      p {
        font-size: 2rem;
      }

    }
  }
}


.footer-item-wrapper {
  display: flex;
  flex-wrap: wrap;
  justify-content: space-evenly;
  width: 100%;
  
}

.footer-item {
  max-width: 30%;
  color: #fff;

  a {
    color: #fff;
    text-decoration: none;
    
    &:hover {
      color: #b1f3ff;
    }
  }

  .useful-links {
    display: flex;
    flex-direction: column;
    padding: 5px;
    margin: 1rem;

    a {
      margin-top: 5px;
    }
  }
}
